module.exports = {
    file_get: function(app, url) {
        app.get(url, require('../routes/' + url).get);
    },
    file_post: function(app, url) {
        app.post(url, require('../routes/' + url).post);
    },
    error_handel: function(res, err, error_status, error_message) {
        console.mongo('Error', err);
        res.sendStatus(error_status).json({
            'status': error_status,
            'message': error_message
        });
    }
};
