var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// making schemas for inserting data in mongodb
var users = Schema({
    first_name: String,
    last_name: String,
    username: String,
    password: String,
    email: String
});
var movie_info = Schema({}, {
    strict: false
});
var rates = Schema({}, {
    strict: false
});
logs = Schema({
    log: String,
    type: String
});
let top_rated_movie = Schema({}, {
    collection: 'top_rated_movie'
});

module.exports = (function() {
    var _return = {};
    _return.movie_info = mongoose.model('movie_info', movie_info);
    _return.users = mongoose.model('users', users);
    _return.top_rated_movie = mongoose.model('top_rated_movie', top_rated_movie);
    _return.rates = mongoose.model('rates', rates);
    _return.logs = mongoose.model('log', logs);
    return _return;
})();
